CREATE TABLE IF NOT EXISTS `R11_Security_test` (
  `x1` varchar(2) NOT NULL DEFAULT '',
  `x2` varchar(2) NOT NULL DEFAULT '',
  `x3` varchar(2) NOT NULL DEFAULT '',
  `x4` varchar(2) NOT NULL,
  `x5` varchar(2) NOT NULL,
  `Security_control` varchar(2048) DEFAULT NULL,
  `operational` varchar(10) DEFAULT NULL,
  `not_operational` varchar(10) DEFAULT NULL,
  `comments` varchar(2048) DEFAULT NULL,
  `SIDSS_ref` text,
  `testing_procedures` varchar(2048) DEFAULT NULL,
  PRIMARY KEY (`x1`,`x2`,`x3`,`x4`,`x5`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;
/*!40101 SET character_set_client = @saved_cs_client */;
REPLACE INTO `R11_Security_test` (`x1`, `x2`, `x3`, `x4`, `x5`, `Security_control`, `operational`, `not_operational`, `comments`, `SIDSS_ref`, `testing_procedures`) VALUES ('R','11','1','','','Implement processes to test for the presence of wireless access points (802.11), and detect and identify all authorized and unauthorized wireless access points on a quarterly basis.','1',NULL,NULL,NULL,'<b>11.1.a</b> Examine policies and procedures to verify processes are defined for detection and identification of both authorized and unauthorized wireless access points on a quarterly basis.<br/><b>11.1.b</b> Verify that the methodology is adequate to detect and identify any unauthorized wireless access points, including at least the following:<ul><li>WLAN cards inserted into system components</li><li>Portable or mobile devices attached to system components to create a wireless access point (for example, by USB, etc.)</li><li>Wireless devices attached to a network port or network device.</li></ul><b>11.1.c</b> Examine output from recent wireless scans to verify that:<ul><li>Authorized and unauthorized wireless access points are identified, and</li><li>The scan is performed at least quarterly for all system components and facilities.</li></ul><b>11.1.d</b> If automated monitoring is utilized (for example, wireless IDS/IPS, NAC, etc.), verify the configuration will generate alerts to notify personnel.'),('R','11','1','1','','Maintain an inventory of authorized wireless access points including a documented business justification.','1',NULL,NULL,NULL,'11.1.1 Examine documented records to verify that an inventory of authorized wireless access points is maintained and a business justification is documented for all authorized wireless access points.'),('R','11','1','2','','Implement incident response procedures in the event unauthorized wireless access points are detected.','1',NULL,NULL,NULL,'<b>11.1.2.a</b> Examine the organization\'s incident response plan (Requirement 12.10) to verify it defines and requires a response in the event that an unauthorized wireless access point is detected.<br/><b>11.1.2.b</b> Interview responsible personnel and/or inspect recent wireless scans and related responses to verify action is taken when unauthorized wireless access points are found.'),('R','11','2','','','Run internal and external network vulnerability scans at least quarterly and after any significant change in the network (such as new system component installations, changes in network topology, firewall rule modifications, product upgrades).','1',NULL,NULL,NULL,'<b>11.2</b> Examine scan reports and supporting documentation to verify that internal and external vulnerability scans are performed as follows:'),('R','11','2','1','','Perform quarterly internal vulnerability scans and rescans as needed, until all \"high-risk\" vulnerabilities (as identified in Requirement 6.1) are resolved. Scans must be performed by qualified personnel.','1',NULL,NULL,NULL,'<b>11.2.1.a</b> Review the scan reports and verify that four quarterly internal scans occurred in the most recent 12-month period.<br/><b>11.2.1.b</b> Review the scan reports and verify that the scan process includes rescans until all \"high-risk\" vulnerabilities as defined in PCI DSS Requirement 6.1 are resolved.<br/><b>11.2.1.c</b> Interview personnel to verify that the scan was performed by a qualified internal resource(s) or qualified external third party, and if applicable, organizational independence of the tester exists (not required to be a QSA or ASV).'),('R','11','2','2','','Perform quarterly external vulnerability scans, via an Approved Scanning Vendor (ASV) approved by the Payment Card Industry Security Standards Council (PCI SSC). Perform rescans as needed, until passing scans are achieved.','1',NULL,NULL,NULL,'<b>11.2.2.a</b> Review output from the four most recent quarters of external vulnerability scans and verify that four quarterly external vulnerability scans occurred in the most recent 12-month period.<br/><b>11.2.2.b</b> Review the results of each quarterly scan and rescan to verify that the ASV Program Guide requirements for a passing scan have been met (for example, no vulnerabilities rated 4.0 or higher by the CVSS, and no automatic failures).<br/><b>11.2.2.c</b> Review the scan reports to verify that the scans were completed by a PCI SSC Approved Scanning Vendor (ASV).'),('R','11','2','3','','Perform internal and external scans, and rescans as needed, after any significant change. Scans must be performed by qualified personnel.','1',NULL,NULL,NULL,'<b>11.2.3.a</b> Inspect and correlate change control documentation and scan reports to verify that system components subject to any significant change were scanned.<br/><b>11.2.3.b</b> Review scan reports and verify that the scan process includes rescans until:<ul><li>For external scans, no vulnerabilities exist that are scored 4.0 or higher by the CVSS.</li><li>For internal scans, all \"high-risk\" vulnerabilities as defined in PCI DSS Requirement 6.1 are resolved.</li></ul><b>11.2.3.c</b> Validate that the scan was performed by a qualified internal resource(s) or qualified external third party, and if applicable, organizational independence of the tester exists (not required to be a QSA or ASV).'),('R','11','3','','','Implement a methodology for penetration testing that includes the following:<ul><li>Is based on industry-accepted penetration testing approaches (for example, NIST SP800-115)</li><li>Includes coverage for the entire CDE perimeter and critical systems</li><li>Includes testing from both inside and outside the network</li><li>Includes testing to validate any segmentation and scope-reduction controls</li><li>Defines application-layer penetration tests to include, at a minimum, the vulnerabilities listed in Requirement 6.5</li><li>Defines network-layer penetration tests to include components that support network functions as well as operating systems</li><li>Includes review and consideration of threats and vulnerabilities experienced in the last 12 months</li><li>Specifies retention of penetration testing results and remediation activities results.</li></ul>','1',NULL,NULL,NULL,'<b>11.3</b> Examine penetration-testing methodology and interview responsible personnel to verify a methodology is implemented that includes the following:<ul><li>Is based on industry-accepted penetration testing approaches (for example, NIST SP800-115)</li><li>Includes coverage for the entire CDE perimeter and critical systems</li><li>Testing from both inside and outside the network</li><li>Includes testing to validate any segmentation and scope-reduction controls</li><li>Defines application-layer penetration tests to include, at a minimum, the vulnerabilities listed in Requirement 6.5</li><li>Defines network-layer penetration tests to include components that support network functions as well as operating systems</li><li>Includes review and consideration of threats and vulnerabilities experienced in the last 12 months</li><li>Specifies retention of penetration testing results and remediation activities results.</li></ul>'),('R','11','3','1','','Perform external penetration testing at least annually and after any significant infrastructure or application upgrade or modification (such as an operating system upgrade, a sub-network added to the environment, or a web server added to the environment).','1',NULL,NULL,NULL,'<b>11.3.1.a</b> Examine the scope of work and results from the most recent external penetration test to verify that penetration testing is performed as follows:<ul><li>Per the defined methodology</li><li>At least annually</li><li>After any significant changes to the environment.</li></ul><b>11.3.1.b</b> Verify that the test was performed by a qualified internal resource or qualified external third party, and if applicable, organizational independence of the tester exists (not required to be a QSA or ASV).'),('R','11','3','2','','Perform internal penetration testing at least annually and after any significant infrastructure or application upgrade or modification (such as an operating system upgrade, a sub-network added to the environment, or a web server added to the environment).','1',NULL,NULL,NULL,'<b>11.3.2.a</b> Examine the scope of work and results from the most recent internal penetration test to verify that penetration testing is performed at least annually and after any significant changes to the environment.<ul><li>Per the defined methodology</li><li>At least annually</li><li>After any significant changes to the environment.</li></ul><b>11.3.2.b</b> Verify that the test was performed by a qualified internal resource or qualified external third party, and if applicable, organizational independence of the tester exists (not required to be a QSA or ASV).'),('R','11','3','3','','Exploitable vulnerabilities found during penetration testing are corrected and testing is repeated to verify the corrections.','1',NULL,NULL,NULL,'<b>11.3.3</b> Examine penetration testing results to verify that noted exploitable vulnerabilities were corrected and that repeated testing confirmed the vulnerability was corrected.'),('R','11','3','4','','If segmentation is used to isolate the CDE from other networks, perform penetration tests at least annually and after any changes to segmentation controls/methods to verify that the segmentation methods are operational and effective, and isolate all out-of-scope systems from in-scope systems.','1',NULL,NULL,NULL,'<b>11.3.4.a</b> Examine segmentation controls and review penetration-testing methodology to verify that penetration-testing procedures are defined to test all segmentation methods to confirm they are operational and effective, and isolate all out-of-scope systems from in-scope systems.<br/><b>11.3.4.b</b> Examine the results from the most recent penetration test to verify that penetration testing to verify segmentation controls:<ul><li>Is performed at least annually and after any changes to segmentation controls/methods.</li><li>Covers all segmentation controls/methods in use.</li><li>Verifies that segmentation methods are operational and effective, and isolate all out-of-scope systems from in-scope systems.</li></ul>'),('R','11','4','','','Use intrusion-detection and/or intrusion-prevention techniques to detect and/or prevent intrusions into the network. Monitor all traffic at the perimeter of the cardholder data environment as well as at critical points in the cardholder data environment, and alert personnel to suspected compromises. Keep all intrusion-detection and prevention engines, baselines, and signatures up to date.','1',NULL,NULL,NULL,'<b>11.4.a</b> Examine system configurations and network diagrams to verify that techniques (such as intrusion-detection systems and/or intrusion-prevention systems) are in place to monitor all traffic:<ul><li>At the perimeter of the cardholder data environment</li><li>At critical points in the cardholder data environment.</li></ul><b>11.4.b</b> Examine system configurations and interview responsible personnel to confirm intrusion-detection and/or intrusion-prevention techniques alert personnel of suspected compromises.</br/><b>11.4.c</b> Examine IDS/IPS configurations and vendor documentation to verify intrusion-detection and/or intrusion-prevention techniques are configured, maintained, and updated per vendor instructions to ensure optimal protection.'),('R','11','5','','','Deploy a change-detection mechanism (for example, file-integrity monitoring tools) to alert personnel to unauthorized modification of critical system files, configuration files, or content files; and configure the software to perform critical file comparisons at least weekly.','1',NULL,NULL,NULL,'<b>11.5.a</b> Verify the use of a change-detection mechanism within the cardholder data environment by observing system settings and monitored files, as well as reviewing results from monitoring activities. Examples of files that should be monitored:<ul><li>System executables</li><li>Application executables</li><li>Configuration and parameter files</li><li>Centrally stored, historical or archived, log and audit files</li><li>Additional critical files determined by entity (for example, through risk assessment or other means).</li></ul><b>11.5.b</b> Verify the mechanism is configured to alert personnel to unauthorized modification of critical files, and to perform critical file comparisons at least weekly.'),('R','11','5','1','','Implement a process to respond to any alerts generated by the change-detection solution.','1',NULL,NULL,NULL,'<b>11.5.1</b> Interview personnel to verify that all alerts are investigated and resolved.'),('R','11','6','','','Ensure that security policies and operational procedures for security monitoring and testing are documented, in use, and known to all affected parties.','1',NULL,NULL,NULL,'<b>11.6</b> Examine documentation interview personnel to verify that security policies and operational procedures for security monitoring and testing are:<ul><li>Documented,</li><li>In use, and</li><li>Known to all affected parties.</li></ul>');
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE IF NOT EXISTS `R12_IS_Policy` (
  `x1` varchar(2) NOT NULL DEFAULT '',
  `x2` varchar(2) NOT NULL DEFAULT '',
  `x3` varchar(2) NOT NULL DEFAULT '',
  `x4` varchar(2) NOT NULL,
  `x5` varchar(2) NOT NULL,
  `Security_control` varchar(2048) DEFAULT NULL,
  `operational` varchar(10) DEFAULT NULL,
  `not_operational` varchar(10) DEFAULT NULL,
  `comments` varchar(2048) DEFAULT NULL,
  `SIDSS_ref` text,
  `testing_procedures` varchar(2048) DEFAULT NULL,
  PRIMARY KEY (`x1`,`x2`,`x3`,`x4`,`x5`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;
/*!40101 SET character_set_client = @saved_cs_client */;
REPLACE INTO `R12_IS_Policy` (`x1`, `x2`, `x3`, `x4`, `x5`, `Security_control`, `operational`, `not_operational`, `comments`, `SIDSS_ref`, `testing_procedures`) VALUES ('R','12','1','','','Establish, publish, maintain and disseminate a security policy','1',NULL,NULL,NULL,'<b>12.1</b> Examine the information security policy and verify that the  policy is published and disseminated to all relevant personnel (including vendors and business partners). '),('R','12','1','1','','Review the security policy at least  annually and update the policy when the  environment changes.','1',NULL,NULL,NULL,'<b>12.1.1</b> Verify that the information security policy is reviewed  at least annually and updated as needed to reflect changes  to business objectives or the risk environment.'),('R','12','10','','','Implement an incident response plan. Be prepared to respond immediately to a system breach.','1',NULL,NULL,NULL,'<b>12.10</b> Examine the incident response plan and related procedures to verify entity is prepared to respond immediately to a system breach by performing the following:'),('R','12','10','1','','Create the incident response plan to be implemented in the event of system breach. Ensure the plan addresses the following, at a minimum:<ul><li>Roles, responsibilities, and communication and contact strategies in the event of a compromise including notification of the payment brands, at a minimum</li><li>Specific incident response procedures</li><li>Business recovery and continuity procedures</li><li>Data backup processes</li><li>Analysis of legal requirements for reporting compromises</li><li>Coverage and responses of all critical system components</li><li>Reference or inclusion of incident response procedures from the payment brands.</li></ul>','1',NULL,NULL,NULL,'<b>12.10.1.a</b> Verify that the incident response plan includes:<ul><li>Roles, responsibilities, and communication strategies in the event of a compromise including notification of the payment brands, at a minimum</li><li>Specific incident response procedures</li><li>Business recovery and continuity procedures</li><li>Data backup processes</li><li>Analysis of legal requirements for reporting compromises (for example, California Bill 1386, which requires notification of affected consumers in the event of an actual or suspected compromise for any business with California residents in their database)</li><li>Coverage and responses for all critical system components</li><li>Reference or inclusion of incident response procedures from the payment brands.</li></ul><b>12.10.1.b</b> Interview personnel and review documentation from a sample of previously reported incidents or alerts to verify that the documented incident response plan and procedures were followed.'),('R','12','10','2','','Test the plan at least annually.','1',NULL,NULL,NULL,'<b>12.10.2</b> Verify that the plan is tested at least annually.'),('R','12','10','3','','Designate specific personnel to be available on a 24/7 basis to respond to alerts.','1',NULL,NULL,NULL,'<b>12.10.3</b> Verify through observation, review of policies, and interviews of responsible personnel that designated personnel are available for 24/7 incident response and monitoring coverage for any evidence of unauthorized activity, detection of unauthorized wireless access points, critical IDS alerts, and/or reports of unauthorized critical system or content file changes.'),('R','12','10','4','','Provide appropriate training to staff with security breach response responsibilities.','1',NULL,NULL,NULL,'<b>12.10.4</b> Verify through observation, review of policies, and interviews of responsible personnel that staff with responsibilities for security breach response are periodically trained.'),('R','12','10','5','','Include alerts from security monitoring systems, including but not limited to intrusion-detection, intrusion-prevention, firewalls, and file-integrity monitoring systems.','1',NULL,NULL,NULL,'<b>12.10.5</b> Verify through observation and review of processes that monitoring and responding to alerts from security monitoring systems, including detection of unauthorized wireless access points, are covered in the incident response plan.'),('R','12','10','6','','Develop a process to modify and evolve the incident response plan according to lessons learned and to incorporate industry developments.','1',NULL,NULL,NULL,'<b>12.10.6</b> Verify through observation, review of policies, and interviews of responsible personnel that there is a process to modify and evolve the incident response plan according to lessons learned and to incorporate industry developments.'),('R','12','2','','','Implement a risk-assessment process that:<ul><li>Is performed at least annually and upon significant changes to the environment (for example, acquisition, merger, relocation, etc.),</li><li>Identifies critical assets, threats, and vulnerabilities, and</li><li>Results in a formal risk assessment.</li></ul>','1',NULL,NULL,NULL,'<b>12.2.a</b> Verify that an annual risk-assessment process is documented that identifies assets, threats, vulnerabilities, and results in a formal risk assessment.<br/><b>12.2.b</b> Review risk-assessment documentation to verify that the risk-assessment process is performed at least annually and upon significant changes to the environment.'),('R','12','3','','','Develop usage policies for critical technologies and define proper use of these technologies. Ensure these usage policies require the following:','1',NULL,NULL,NULL,'<b>12.3</b> Examine the usage policies for critical technologies and interview responsible personnel to verify the following policies are implemented and followed:'),('R','12','3','1','','Explicit approval by authorized parties','1',NULL,NULL,NULL,'<b>12.3.1</b> Verify that the usage policies include processes for explicit approval from authorized parties to use the technologies.'),('R','12','3','10','','For personnel accessing cardholder data via remote-access technologies, prohibit the copying, moving, and storage of cardholder data onto local hard drives and removable electronic media, unless explicitly authorized for a defined business need.<br/>Where there is an authorized business need, the usage policies must require the data be protected in accordance with all applicable PCI DSS Requirements.','1',NULL,NULL,NULL,'<b>12.3.10.a</b> Verify that the usage policies prohibit copying, moving, or storing of cardholder data onto local hard drives and removable electronic media when accessing such data via remote-access technologies.<br/><b>12.3.10.b</b> For personnel with proper authorization, verify that usage policies require the protection of cardholder data in accordance with PCI DSS Requirements.'),('R','12','3','2','','Authentication for use of the technology','1',NULL,NULL,NULL,'<b>12.3.2</b> Verify that the usage policies include processes for all technology use to be authenticated with user ID and password or other authentication item (for example, token).'),('R','12','3','3','','A list of all such devices and personnel with access','1',NULL,NULL,NULL,'<b>12.3.3</b> Verify that the usage policies define a list of all devices and personnel authorized to use the devices.'),('R','12','3','4','','A method to accurately and readily determine owner, contact information, and purpose (for example, labeling, coding, and/or inventorying of devices)','1',NULL,NULL,NULL,'<b>12.3.4</b> Verify that the usage policies define a method to accurately and readily determine owner, contact information, and purpose (for example, labeling, coding, and/or inventorying of devices).'),('R','12','3','5','','Acceptable uses of the technology','1',NULL,NULL,NULL,'<b>12.3.5</b> Verify that the usage policies define acceptable uses for the technology.'),('R','12','3','6','','Acceptable network locations for the technologies','1',NULL,NULL,NULL,'<b>12.3.6</b> Verify that the usage policies define acceptable network locations for the technology.'),('R','12','3','7','','List of company-approved products','1',NULL,NULL,NULL,'<b>12.3.7</b> Verify that the usage policies include a list of company-approved products.'),('R','12','3','8','','Automatic disconnect of sessions for remote-access technologies after a specific period of inactivity','1',NULL,NULL,NULL,'<b>12.3.8.a</b> Verify that the usage policies require automatic disconnect of sessions for remote-access technologies after a specific period of inactivity.<br/><b>12.3.8.b</b> Examine configurations for remote access technologies to verify that remote access sessions will be automatically disconnected after a specific period of inactivity.'),('R','12','3','9','','Activation of remote-access technologies for vendors and business partners only when needed by vendors and business partners, with immediate deactivation after use','1',NULL,NULL,NULL,'<b>12.3.9</b> Verify that the usage policies require activation of remote-access technologies used by vendors and business partners only when needed by vendors and business partners, with immediate deactivation after use.'),('R','12','4','','','Ensure that the security policy and procedures clearly define information security responsibilities for all personnel.','1',NULL,NULL,NULL,'<b>12.4.a</b> Verify that information security policies clearly define information security responsibilities for all personnel.<br/>12.4.b Interview a sample of responsible personnel to verify they understand the security policies.'),('R','12','5','','','Assign to an individual or team the following information security management responsibilities:','1',NULL,NULL,NULL,'<b>12.5</b> Examine information security policies and procedures to verify:<ul><li>The formal assignment of information security to a Chief Security Officer or other security-knowledgeable member of management.</li><li>The following information security responsibilities are specifically and formally assigned:</li></ul>'),('R','12','5','1','','Establish, document, and distribute security policies and procedures.','1',NULL,NULL,NULL,'<b>12.5.1</b> Verify that responsibility for establishing, documenting and distributing security policies and procedures is formally assigned.'),('R','12','5','2','','Monitor and analyze security alerts and information, and distribute to appropriate personnel.','1',NULL,NULL,NULL,'<b>12.5.2</b> Verify that responsibility for monitoring and analyzing security alerts and distributing information to appropriate information security and business unit management personnel is formally assigned.'),('R','12','5','3','','Establish, document, and distribute security incident response and escalation procedures to ensure timely and effective handling of all situations.','1',NULL,NULL,NULL,'<b>12.5.3</b> Verify that responsibility for establishing, documenting, and distributing security incident response and escalation procedures is formally assigned.'),('R','12','5','4','','Administer user accounts, including additions, deletions, and modifications.','1',NULL,NULL,NULL,'<b>12.5.4</b> Verify that responsibility for administering (adding, deleting, and modifying) user account and authentication management is formally assigned.'),('R','12','5','5','','Monitor and control all access to data.','1',NULL,NULL,NULL,'<b>12.5.5</b> Verify that responsibility for monitoring and controlling all access to data is formally assigned.'),('R','12','6','','','Implement a formal security awareness program to make all personnel aware of the importance of cardholder data security.','1',NULL,NULL,NULL,'<b>12.6.a</b> Review the security awareness program to verify it provides awareness to all personnel about the importance of cardholder data security.<br/><b>12.6.b</b> Examine security awareness program procedures and documentation and perform the following:'),('R','12','6','1','','Educate personnel upon hire and at least annually.','1',NULL,NULL,NULL,'<b>12.6.1.a</b> Verify that the security awareness program provides multiple methods of communicating awareness and educating personnel (for example, posters, letters, memos, web-based training, meetings, and promotions).<br/><b>12.6.1.b</b> Verify that personnel attend security awareness training upon hire and at least annually.<br/><b>12.6.1.c</b> Interview a sample of personnel to verify they have completed awareness training and are aware of the importance of cardholder data security.'),('R','12','6','2','','Require personnel to acknowledge at least annually that they have read and understood the security policy and procedures.','1',NULL,NULL,NULL,'<b>12.6.2</b> Verify that the security awareness program requires personnel to acknowledge, in writing or electronically, at least annually, that they have read and understand the information security policy.'),('R','12','7','','','Screen potential personnel prior to hire to minimize the risk of attacks from internal sources. (Examples of background checks include previous employment history, criminal record, credit history, and reference checks.)','1',NULL,NULL,NULL,'<b>12.7</b> Inquire with Human Resource department management and verify that background checks are conducted (within the constraints of local laws) prior to hire on potential personnel who will have access to cardholder data or the cardholder data environment.'),('R','12','8','','','Maintain and implement policies and procedures to manage service providers with whom cardholder data is shared, or that could affect the security of cardholder data, as follows:','1',NULL,NULL,NULL,'<b>12.8</b> Through observation, review of policies and procedures, and review of supporting documentation, verify that processes are implemented to manage service providers with whom cardholder data is shared, or that could affect the security of cardholder data (for example, backup tape storage facilities, managed service providers such as web-hosting companies or security service providers, those that receive data for fraud modeling purposes, etc.), as follows:'),('R','12','8','1','','Maintain a list of service providers.','1',NULL,NULL,NULL,'<b>12.8.1</b> Verify that a list of service providers is maintained.'),('R','12','8','2','','Maintain a written agreement that includes an acknowledgement that the service providers are responsible for the security of cardholder data the service providers possess or otherwise store, process or transmit on behalf of the customer, or to the extent that they could impact the security of the customer\'s cardholder data environment.','1',NULL,NULL,NULL,'<b>12.8.2</b> Observe written agreements and confirm they include an acknowledgement by service providers that they are responsible for the security of cardholder data the service providers possess or otherwise store, process or transmit on behalf of the customer, or to the extent that they could impact the security of the customer\'s cardholder data environment.'),('R','12','8','3','','Ensure there is an established process for engaging service providers including proper due diligence prior to engagement.','1',NULL,NULL,NULL,'<b>12.8.3</b> Verify that policies and procedures are documented and implemented including proper due diligence prior to engaging any service provider.'),('R','12','8','4','','Maintain a program to monitor service providers\' PCI DSS compliance status at least annually.','1',NULL,NULL,NULL,'<b>12.8.4</b> Verify that the entity maintains a program to monitor its service providers\' PCI DSS compliance status at least annually.'),('R','12','8','5','','Maintain information about which PCI DSS requirements are managed by each service provider, and which are managed by the entity.','1',NULL,NULL,NULL,'<b>12.8.5</b> Verify the entity maintains information about which PCI DSS requirements are managed by each service provider, and which are managed by the entity.'),('R','12','9','','','Additional requirement for service providers: Service providers acknowledge in writing to customers that they are responsible for the security of cardholder data the service provider possesses or otherwise stores, processes, or transmits on behalf of the customer, or to the extent that they could impact the security of the customer\'s cardholder data environment.','1',NULL,NULL,NULL,'<b>12.9</b> Additional testing procedure for service providers: Review service provider\'s policies and procedures and observe written agreement templates to confirm the service provider acknowledges in writing to customers that the service provider will maintain all applicable PCI DSS requirements to the extent the service provider handles, has access to, or otherwise stores, processes or transmits the customer\'s cardholder data or sensitive authentication data, or manages the customer\'s cardholder data environment on behalf of a customer');

USE alienvault_siem;

-- CALL delete_events ('delete_tmp_table')
DELIMITER $$
DROP PROCEDURE IF EXISTS delete_events$$
CREATE PROCEDURE delete_events( tmp_table VARCHAR(64) )
BEGIN
    SET AUTOCOMMIT = 0;
    
    SET @query = CONCAT('DELETE aux FROM alienvault_siem.reputation_data aux LEFT JOIN ',tmp_table,' tmp ON tmp.id=aux.event_id WHERE tmp.id IS NOT NULL');
    PREPARE sql_query from @query;
    EXECUTE sql_query;
    DEALLOCATE PREPARE sql_query;

    SET @query = CONCAT('DELETE aux FROM alienvault_siem.idm_data aux LEFT JOIN ',tmp_table,' tmp ON tmp.id=aux.event_id WHERE tmp.id IS NOT NULL');
    PREPARE sql_query from @query;
    EXECUTE sql_query;
    DEALLOCATE PREPARE sql_query;

    SET @query = CONCAT('DELETE aux FROM alienvault_siem.extra_data aux LEFT JOIN ',tmp_table,' tmp ON tmp.id=aux.event_id WHERE tmp.id IS NOT NULL');
    PREPARE sql_query from @query;
    EXECUTE sql_query;
    DEALLOCATE PREPARE sql_query;

    SET @query = CONCAT('DELETE aux FROM alienvault_siem.acid_event aux LEFT JOIN ',tmp_table,' tmp ON tmp.id=aux.id WHERE tmp.id IS NOT NULL');
    PREPARE sql_query from @query;
    EXECUTE sql_query;
    DEALLOCATE PREPARE sql_query;

    COMMIT;
END$$

CREATE TABLE IF NOT EXISTS `ah_acid_event` (
  `ctx` BINARY(16) NOT NULL,
  `device_id` INT UNSIGNED NOT NULL,
  `timestamp` DATETIME NOT NULL,
  `src_host` BINARY(16) NOT NULL DEFAULT 0x0,
  `dst_host` BINARY(16) NOT NULL DEFAULT 0x0,
  `src_net` BINARY(16) NOT NULL DEFAULT 0x0,
  `dst_net` BINARY(16) NOT NULL DEFAULT 0x0,
  `cnt` INT UNSIGNED NOT NULL DEFAULT 0,
  INDEX `day` (`timestamp` ASC),
  PRIMARY KEY (`ctx`, `device_id`, `timestamp`, `src_host`, `dst_net`, `dst_host`, `src_net`),
  INDEX `src_host` (`src_host` ASC),
  INDEX `dst_host` (`dst_host` ASC))
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8$$

DROP TRIGGER IF EXISTS `count_acid_event`$$
CREATE TRIGGER `count_acid_event` AFTER INSERT ON `acid_event` FOR EACH ROW
-- Edit trigger body code below this line. Do not edit lines above this one
BEGIN

  INSERT IGNORE INTO ac_acid_event (ctx, device_id, plugin_id, plugin_sid, day, src_host, dst_host, src_net, dst_net, cnt)
  VALUES (NEW.ctx, NEW.device_id, NEW.plugin_id, NEW.plugin_sid, DATE(NEW.timestamp), NEW.src_host, NEW.dst_host, NEW.src_net, NEW.dst_net, 1)
  ON DUPLICATE KEY UPDATE cnt = cnt + 1;

  INSERT IGNORE INTO ah_acid_event (ctx, device_id, timestamp, src_host, dst_host, src_net, dst_net, cnt)
  VALUES (NEW.ctx, NEW.device_id, DATE_FORMAT(NEW.timestamp, '%Y-%m-%d %H:00:00'), NEW.src_host, NEW.dst_host, NEW.src_net, NEW.dst_net, 1)
  ON DUPLICATE KEY UPDATE cnt = cnt + 1;

END
$$

DROP TRIGGER IF EXISTS `del_count_acid_event`$$
CREATE TRIGGER `del_count_acid_event` AFTER DELETE ON `acid_event` FOR EACH ROW
-- Edit trigger body code below this line. Do not edit lines above this one
BEGIN

  UPDATE ac_acid_event SET cnt = cnt - 1
  WHERE ctx = OLD.ctx AND device_id = OLD.device_id AND plugin_id = OLD.plugin_id AND plugin_sid = OLD.plugin_sid
     AND day = DATE(OLD.timestamp)
     AND src_host = IFNULL(OLD.src_host, 0x00000000000000000000000000000000)
     AND dst_host = IFNULL(OLD.dst_host, 0x00000000000000000000000000000000)
     AND src_net = IFNULL(OLD.src_net, 0x00000000000000000000000000000000)
     AND dst_net = IFNULL(OLD.dst_net, 0x00000000000000000000000000000000) AND cnt > 0;

  UPDATE ah_acid_event SET cnt = cnt - 1
  WHERE ctx = OLD.ctx AND device_id = OLD.device_id
     AND timestamp = DATE_FORMAT(OLD.timestamp, '%Y-%m-%d %H:00:00')
     AND src_host = IFNULL(OLD.src_host, 0x00000000000000000000000000000000)
     AND dst_host = IFNULL(OLD.dst_host, 0x00000000000000000000000000000000)
     AND src_net = IFNULL(OLD.src_net, 0x00000000000000000000000000000000)
     AND dst_net = IFNULL(OLD.dst_net, 0x00000000000000000000000000000000) AND cnt > 0;
END
$$

