USE alienvault;
SET AUTOCOMMIT=0;

-- modify/add columns procedure
DROP PROCEDURE IF EXISTS addcol;
DELIMITER $$
CREATE PROCEDURE addcol() BEGIN
  IF NOT EXISTS
      (SELECT * FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_SCHEMA = 'PCI' AND TABLE_NAME = 'R12_IS_Policy' AND COLUMN_NAME = 'x5')
  THEN
        ALTER TABLE PCI.`R01_FW_Config` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R01_FW_Config` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R02_Vendor_default` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R02_Vendor_default` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R03_Stored_cardholder` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R03_Stored_cardholder` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R04_Data_encryption` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R04_Data_encryption` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R05_Antivirus` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R05_Antivirus` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R06_System_app` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R06_System_app` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R07_Access_control` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R07_Access_control` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R08_UniqueID` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R08_UniqueID` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R09_Physical_Access` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R09_Physical_Access` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R10_Monitoring` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R10_Monitoring` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R11_Security_test` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R11_Security_test` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);

        ALTER TABLE PCI.`R12_IS_Policy` ADD `x5` VARCHAR(2) NOT NULL AFTER `x4`;
        ALTER TABLE PCI.`R12_IS_Policy` DROP PRIMARY KEY, ADD PRIMARY KEY (`x1`, `x2`, `x3`, `x4`, `x5`);
  END IF; 
END$$

DELIMITER ;
